import React , {useEffect , useState} from "react";

// import { positions } from "../data/data";

import axios from "axios"; //use to connect to an endpoint of an API

const Positions = () => {
  
  const [allPositions, setAllPositions] = useState([]); //initially array is empty

  useEffect(() => {
    // axios.get("http://localhost:4000/allPositions").then((res) => {
    axios.get("https://zerodha-backend-yn9o.onrender.com/allPositions").then((res) => {
      console.log(res.data);
      setAllPositions(res.data);
    });
  }, []);
  // https://zerodha-backend-yn9o.onrender.com
  return (
    <>
      <h3 className="title">Positions ({allPositions.length})</h3>

      <div className="order-table">
        <table>
          <tr>
            <th>Product</th>
            <th>Instrument</th>
            <th>Qty.</th>
            <th>Avg.</th>
            <th>LTP</th>
            <th>P&L</th>
            <th>Chg.</th>
          </tr>
          
          {allPositions.map((stock,index)=>{
            const curValue = stock.price * stock.qty;
            const isProfit = curValue - stock.avg * stock.qty >= 0.0;
            const profClass = isProfit ? "profit" : "loss";
            const dayClass = stock.isLoss ? "loss" : "profit";

            return(
            <tr key={index} >
              <td>{stock.product}</td>
              <td>{stock.name}</td>
              <td>{stock.qty}</td>
              <td>{stock.avg.toFixed(2)}</td>
              <td>{stock.price.toFixed(2)}</td>
              <td className={profClass}>{(curValue - stock.avg * stock.qty).toFixed(2)}</td>
              <td className={dayClass}>{stock.day}</td>
          </tr>
          )
          })}            

        </table>
      </div>
    </>
  );
};

export default Positions
